pragma solidity 0.8.17;

import {IRebornPortal} from "src/interfaces/IRebornPortal.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {SafeERC20Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import {SafeOwnableUpgradeable} from "@p12/contracts-lib/contracts/access/SafeOwnableUpgradeable.sol";

import {IRebornToken} from "src/interfaces/IRebornToken.sol";

contract RebornPortal is
    IRebornPortal,
    SafeOwnableUpgradeable,
    UUPSUpgradeable
{
    using SafeERC20Upgradeable for IRebornToken;
    error InsufficientAmount();

    /** you need buy a soup before reborn */
    uint256 public soupPrice = 0.1 * 1 ether;

    /**
     * @dev talent and property price in compact mode
     * @dev talant price first 8 bytes then property 8 bytes
     * @dev  4 2 0 for talent   6 4 2 1 0 for property
     */
    uint256 private _price = 0x00000000000004200000000000064210;

    IRebornToken public rebornToken;

    function initialize(
        IRebornToken rebornToken_,
        uint256 soupPrice_,
        uint256 price_,
        address owner_
    ) public initializer {
        rebornToken = rebornToken_;
        soupPrice = soupPrice_;
        _price = price_;
        __Ownable_init(owner_);
    }

    // solhint-disable-next-line no-empty-blocks
    function _authorizeUpgrade(address newImplementation)
        internal
        override
        onlyOwner
    {}

    function incarnate(Innate memory innate) external payable override {
        _incarnate(innate);
    }

    function incarnate(
        Innate memory innate,
        uint256 amount,
        uint256 deadline,
        bytes32 r,
        bytes32 s,
        uint8 v
    ) external payable override {
        _permit(amount, deadline, r, s, v);
        _incarnate(innate);
    }

    /**
     * @dev engrave the result on chain and reward
     */
    function engrave() external override {}

    /**
     * @dev set soup price
     */
    function setSoupPrice(uint256 price) external override onlyOwner {
        soupPrice = price;
        emit NewSoupPrice(price);
    }

    /**
     * @dev set other price
     */
    function setPrice(uint256 price) external override onlyOwner {
        _price = price;
        emit NewPrice(price);
    }

    /**
     * @dev run erc20 permit to approve
     */
    function _permit(
        uint256 amount,
        uint256 deadline,
        bytes32 r,
        bytes32 s,
        uint8 v
    ) internal {
        rebornToken.permit(
            msg.sender,
            address(this),
            amount,
            deadline,
            v,
            r,
            s
        );
    }

    /**
     * @dev implementation of incarnate
     */
    function _incarnate(Innate memory innate) internal {
        if (msg.value < soupPrice) {
            revert InsufficientAmount();
        }
        // transfer redundant native token back
        payable(msg.sender).transfer(msg.value - soupPrice);

        // reborn token needed
        uint256 rbtAmount = talentPrice(innate.talent) +
            propertiesPrice(innate.properties);

        rebornToken.transferFrom(msg.sender, address(this), rbtAmount);

        emit Incarnate(innate.talent, innate.properties);
    }

    /**
     * @dev calculate talent price in $REBORN for each talent
     */
    function talentPrice(TALANT talent) public view returns (uint256) {
        return (((_price >> 64) >> (uint256(talent) * 4)) & 0xf) * 1 ether;
    }

    /**
     * @dev calculate properties price in $REBORN for each properties
     */
    function propertiesPrice(PROPERTIES properties)
        public
        view
        returns (uint256)
    {
        return ((_price >> (uint256(properties) * 4)) & 0xf) * 1 ether;
    }
}
","keccak256":"0x084e696e72a428fa46a135d443c4f35f34825965c3d68ac19daea7470adb2590","license":"GPL-3.0-or-later"},"src/interfaces/IRebornPortal.sol":{"content":"// SPDX-License-Identifier: GPL-3.0-or-later
pragma solidity 0.8.17;

interface IRebornDefination {
    enum TALANT {
        Degen,
        Gifted,
        Genius
    }

    enum PROPERTIES {
        BASIC,
        C,
        B,
        A,
        S
    }

    struct Innate {
        TALANT talent;
        PROPERTIES properties;
    }

    event Incarnate(TALANT indexed talent, PROPERTIES indexed properties);

    event NewSoupPrice(uint256 price);

    event NewPrice(uint256 price);
}

interface IRebornPortal is IRebornDefination {
    /** init enter and buy */
    function incarnate(Innate memory innate) external payable;

    /** init enter and buy with permit signature */
    function incarnate(
        Innate memory innate,
        uint256 amount,
        uint256 deadline,
        bytes32 r,
        bytes32 s,
        uint8 v
    ) external payable;

    /** save data on chain and get reward */
    function engrave() external;

    /** set soup price */
    function setSoupPrice(uint256 price) external;

    /** set others price */
    function setPrice(uint256 price) external;
}
