    function render(
        string calldata seed,
        uint256 lifeScore,
        uint256 round,
        uint256 age,
        address addr,
        uint256 pay,
        uint256 reward
    ) public pure returns (string memory) {
        string memory Part1 = _renderPart1(seed, lifeScore, round, age);
        string memory Part2 = _renderPart2(addr, pay, reward);
        return string(abi.encodePacked(Part1, Part2));
    }

    function _renderPart1(
        string calldata seed,
        uint256 lifeScore,
        uint256 round,
        uint256 age
    ) internal pure returns (string memory) {
        return
            string(
                abi.encodePacked(
                    P1,
                    seed,
                    P2,
                    Strings.toString(lifeScore),
                    P3,
                    Strings.toString(round),
                    P4,
                    Strings.toString(age)
                )
            );
    }

    function _renderPart2(
        address addr,
        uint256 pay,
        uint256 reward
    ) internal pure returns (string memory) {
        return
            string(
                abi.encodePacked(
                    P5,
                    _shortenAddr(addr),
                    P6,
                    Strings.toString(pay),
                    P7,
                    Strings.toString(reward),
                    P8
                )
            );
    }
}
